package com.ds.ms.address.controller;import com.ds.ms.address.dto.StateDto;import com.ds.ms.address.mappers.StateMapper;import com.ds.ms.address.repository.StatesRepository;import lombok.AllArgsConstructor;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.*;@AllArgsConstructor@RequestMapping("/state")@RestControllerpublic class StateController {    private final StatesRepository statesRepository;    private final StateMapper stateMapper;    /*    @PostMapping(produces = MediaType.APPLICATION_JSON_VALUE, consumes = MediaType.APPLICATION_JSON_VALUE)    public VehicleDtoWithId savePerson(@RequestBody VehicleDto vehicleDto) {        return vehicleService.saveVehicle(vehicleDto);    }    @PutMapping(produces = MediaType.APPLICATION_JSON_VALUE,            consumes = MediaType.APPLICATION_JSON_VALUE)    public VehicleDtoWithId updatePerson(@RequestBody VehicleDtoWithId vehicleDtoWithId) {        return vehicleService.updateVehicle(vehicleDtoWithId);    }     */    @GetMapping(value = "/id/{id}", produces = MediaType.APPLICATION_JSON_VALUE)    public StateDto findById(@PathVariable("id") Long id) {        return this.stateMapper.stateToStateDto(this.statesRepository.findById(id).get());    }    @GetMapping(value = "/count", produces = MediaType.APPLICATION_JSON_VALUE)    public long countStates() {        return this.statesRepository.count();    }    @GetMapping(value = "/name/{name}", produces = MediaType.APPLICATION_JSON_VALUE)    public StateDto findByStateName(@PathVariable("name") String stateName) {        return this.stateMapper.stateToStateDto(this.statesRepository.findByName(stateName).get());    }    /*    @GetMapping(value = "/plate/{vehiclePlate}", produces = MediaType.APPLICATION_JSON_VALUE)    public VehicleDtoWithId findByPlate(@PathVariable("vehiclePlate") String vehiclePlate) {        return this.vehicleService.findByPlate(vehiclePlate);    }     */    @GetMapping(value = "/ping")    public String findByName() {        return "pong from state controller";    }}