package com.ds.ms.article.controller;import com.ds.ms.article.dto.CountrySmallDto;import com.ds.ms.article.dto.CountryDto;import com.ds.ms.article.mappers.CountryMapper;import com.ds.ms.article.repository.CountriesRepository;import lombok.AllArgsConstructor;import org.springframework.http.MediaType;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@AllArgsConstructor@RequestMapping("/country")@RestControllerpublic class CountryController {    private final CountriesRepository countriesRepository;    private final CountryMapper countryMapper;    /*    @PostMapping(produces = MediaType.APPLICATION_JSON_VALUE, consumes = MediaType.APPLICATION_JSON_VALUE)    public VehicleDtoWithId savePerson(@RequestBody VehicleDto vehicleDto) {        return vehicleService.saveVehicle(vehicleDto);    }    @PutMapping(produces = MediaType.APPLICATION_JSON_VALUE,            consumes = MediaType.APPLICATION_JSON_VALUE)    public VehicleDtoWithId updatePerson(@RequestBody VehicleDtoWithId vehicleDtoWithId) {        return vehicleService.updateVehicle(vehicleDtoWithId);    }     */    @GetMapping(value = "/id/{id}", produces = MediaType.APPLICATION_JSON_VALUE)    public CountryDto findById(@PathVariable("id") Long id) {        return this.countryMapper.countryToCountryDto(this.countriesRepository.findById(id).get());    }    @GetMapping(value = "/count", produces = MediaType.APPLICATION_JSON_VALUE)    public long count() {        return this.countriesRepository.count();    }    @GetMapping(value = "/name/{name}", produces = MediaType.APPLICATION_JSON_VALUE)    public CountryDto findByName(@PathVariable("name") String coutryName) {        return this.countryMapper.countryToCountryDto(this.countriesRepository.findByName(coutryName).get());    }    @GetMapping(value = "/ping")    public String findByName() {        return "pong from country controller";    }    @GetMapping(value = "/all", produces = MediaType.APPLICATION_JSON_VALUE)    public List<CountrySmallDto> findAll() {        return this.countryMapper.countrySmallDtoListToCountryDtoList(this.countriesRepository.findAll());    }    /*    @GetMapping(value = "/plate/{vehiclePlate}", produces = MediaType.APPLICATION_JSON_VALUE)    public VehicleDtoWithId findByPlate(@PathVariable("vehiclePlate") String vehiclePlate) {        return this.vehicleService.findByPlate(vehiclePlate);    }     */}